mapper.select:
SELECT
	COUNT(*)
FROM basket 
WHERE 1=1
AND bsk_mmSeq = #{mmSeq}
AND bsk_prSeq = #{prSeq}

mapper.insert:
INSERT INTO basket (
	bsk_mmSeq
	,bsk_prSeq
	,bskAmount
	,bskSell_Price
)
VALUES (
	#{mmSeq}
	,#{prSeq}
	,#{bskAmount}
	,#{bskSell_Price}
)

		SELECT last_insert_id()

mapper.delete:
	DELETE
	FROM basket 
    WHERE bsk_mmSeq = #{mmSeq}
    AND bsk_prSeq = #{prSeq}

mapper.update:
UPDATE basket
SET
	bskAmount		= #{bskAmount}
	,bskSell_Price	= #{bskSell_Price}	
WHERE 1=1
AND bskSeq = #{bskSeq}
