mapper.insert:
INSERT INTO address (
  	adrName
  	,adrZipcode
  	,adrAddress
  	,adrAddressDetail
  	,adrPhoneNumber
  	,adrTel
  	,adrdefaultNy
  	,adr_mmSeq
)
VALUES (
  	#{mmName}
  	,#{mmZipcode}
  	,#{mmAddress}
  	,#{mmAddressDetail}
  	,#{mmPhoneNumber}
  	,#{adrTel}
  	,1
  	,#{mmSeq}
)

	SELECT last_insert_id()

INSERT INTO address (
	adrAddrName
  	,adrName
  	,adrZipcode
  	,adrAddress
  	,adrAddressDetail
  	,adrPhoneNumber
  	,adrTel
  	,adrdefaultNy
  	,adr_mmSeq
)
VALUES (
	#{adrAddrName}
  	,#{adrName}
  	,#{adrZipcode}
  	,#{adrAddress}
  	,#{adrAddressDetail}
  	,#{adrPhoneNumber}
  	,#{adrTel}
  	,#{adrdefaultNy}
  	,#{mmSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE address
SET
	adrAddrName			= #{adrAddrName}
  	,adrName			= #{adrName}
  	,adrZipcode			= #{adrZipcode}
  	,adrAddress			= #{adrAddress}
  	,adrAddressDetail	= #{adrAddressDetail}
  	,adrPhoneNumber		= #{adrPhoneNumber}
  	,adrTel				= #{adrTel}
  	,adrdefaultNy		= #{adrdefaultNy}
WHERE adrSeq = #{adrSeq}

mapper.delete:
	DELETE
	FROM address 
    WHERE adrSeq = #{adrSeq}
    AND adr_mmSeq = #{mmSeq}
