mapper.insert:
INSERt INTO member (
	mmId
	,mmPassword
	,mmName
	,mmTelecom
	,mmPhoneNumber
	,mmSmsRecieveCheckNy
	,mmEmail
	,mmEmailRecieveCheckNy
	,mmGender
	,mmYear
	,mmBirth
	,mmGrade
	,mmCertNy
	,mmCreDate
	,mmDelNy
	,mmAdminNy
)
VALUES (
	#{mmId}
	,#{mmPassword}
	,#{mmName}
	,#{mmTelecom}
	,#{mmPhoneNumber}
	,0
	,#{mmEmail}
	,0
	,#{mmGender}
	,#{mmYear}
	,#{mmBirth}
	,1
	,#{mmCertNy}
	,now()
	,0
	,0
)

	SELECT last_insert_id()

INSERT INTO address (
  	adrName
  	,adrZipcode
  	,adrAddress
  	,adrAddressDetail
  	,adrPhoneNumber
  	,adrTel
  	,adrdefaultNy
  	,adr_mmSeq
)
VALUES (
  	#{mmName}
  	,#{mmZipcode}
  	,#{mmAddress}
  	,#{mmAddressDetail}
  	,#{mmPhoneNumber}
  	,#{adrTel}
  	,1
  	,#{mmSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE address
SET
  	adrName				= #{mmName}
  	,adrZipcode			= #{mmZipcode}
  	,adrAddress			= #{mmAddress}
  	,adrAddressDetail	= #{mmAddressDetail}
  	,adrPhoneNumber		= #{mmPhoneNumber}
  	,adrTel				= #{mmTel}
  	,adrdefaultNy		= 1
WHERE adrSeq = #{adrSeq}

mapper.insert:
INSERT INTO address (
	adrAddrName
