mapper.delete:
	DELETE
	FROM address 
    WHERE adrSeq = #{adrSeq}
    AND adr_mmSeq = #{mmSeq}

mapper.update:
UPDATE member
SET
	mmCertNy = 1
	,mmPhoneNumber = #{mmPhoneNumber}
	,mmTelecom = #{mmTelecom}
WHERE 1=1
AND mmSeq = #{mmSeq}
