mapper.insert:
    	INSERT INTO product (
    		prName
    		,prDiscount
    		,prPrice
    		,prInfo
    		,prKind
    		,prFragrance
    		,prUseNy
    		,prDelNy
    		,prTotalPrice
    	)

    	VALUES (
    		#{prName}
    		,#{prDiscount}
    		,#{prPrice}
    		,#{prInfo}
    		,#{prKind}
    		,#{prFragrance}
    		,#{prUseNy}
    		,#{prDelNy}
    		,#{prTotalPrice}
    	)

			SELECT last_insert_id()
